import React, { useState } from 'react'
import './Resume.css'

import Header from './Header/Header'
import Footer from './Footer/Footer'
import Skills from './Skills/Skills'
import Education from './Education/Education'
import Portfolio from './Portfolio/Portfolio'
import Job from './Job/Job'

const Resume = () => {
  const [selectedSkill, setSelectedSkill] = useState(null)
  const [selectedPort, setSelectedPort] = useState(null)
  const [selectedEdu, setSelectedEdu] = useState(null)
  const [selectedJob, setSelectedJob] = useState(null)

  const toggleSkill = (checkSkill) => {
    if(selectedSkill === checkSkill) {
      return setSelectedSkill(null)
    }

    setSelectedSkill(checkSkill)
  }

  const togglePort = (checkPort) => {
    if(selectedPort === checkPort) {
      return setSelectedPort(null)
    }

    setSelectedPort(checkPort)
  }

  const toggleEdu = (checkEdu) => {
    if(selectedEdu === checkEdu) {
      return setSelectedEdu(null)
    }

    setSelectedEdu(checkEdu)
  }

  const toggleJob = (checkJob) => {
    if(selectedJob === checkJob) {
      return setSelectedJob(null)
    }

    setSelectedJob(checkJob)
  }

  return (
    <div className="wrapper">
      <div className="accordion">
        <div className="header">
          <Header />
        </div>
        <div className="itemSkill">
          <div className="title" onClick={() => toggleSkill("skill")}>
            <div className="titlePadding">
              <h2>SKILLS</h2>
              <span>{selectedSkill === "skill" ? "-" : "+"}</span>
            </div>

          </div>
          <div className={selectedSkill === "skill" ? "content show" : "content"}>
            <Skills />
          </div>
        </div>

        <div className="itemEdu">
          <div className="title" onClick={() => toggleEdu("edu")}>
            <h2>EDUCATION</h2>
            <span>{selectedEdu === "edu" ? "-" : "+"}</span>
          </div>
          <div className={selectedEdu === "edu" ? "content show" : "content"}>
            <Education />
          </div>
        </div>

        <div className="itemPort">
          <div className="title" onClick={() => togglePort("port")}>
            <h2>PORTFOLIO</h2>
            <span>{selectedPort === "port" ? "-" : "+"}</span>
          </div>
          <div className={selectedPort === "port" ? "content show" : "content"}>
            <Portfolio />
          </div>
        </div>

        <div className="itemJob">
          <div className="title" onClick={() => toggleJob("job")}>
            <h2>EXPERIENCE</h2>
            <span>{selectedJob === "job" ? "-" : "+"}</span>
          </div>
          <div className={selectedJob === "job" ? "content show" : "content"}>
            <Job />
          </div>
        </div>

        <div className="footer">
          <Footer />
        </div>
      </div>
    </div>
  )
}

export default Resume
